import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{o as s,c as t,d as i}from"./app-7479d48d.js";const a={},l=i('<h1 id="engineering-fundamentals-checklist" tabindex="-1"><a class="header-anchor" href="#engineering-fundamentals-checklist" aria-hidden="true">#</a> Engineering Fundamentals Checklist</h1><p>This checklist is excerpted from Microsoft and is very useful for reference.</p><h2 id="source-control" tabindex="-1"><a class="header-anchor" href="#source-control" aria-hidden="true">#</a> Source Control</h2><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-0" disabled="disabled"><label class="task-list-item-label" for="task-item-0"> The default target branch is locked.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-1" disabled="disabled"><label class="task-list-item-label" for="task-item-1"> Merges are done through PRs.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-2" disabled="disabled"><label class="task-list-item-label" for="task-item-2"> PRs reference related work items.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-3" disabled="disabled"><label class="task-list-item-label" for="task-item-3"> Commit history is consistent and commit messages are informative (what, why).</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-4" disabled="disabled"><label class="task-list-item-label" for="task-item-4"> Consistent branch naming conventions.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-5" disabled="disabled"><label class="task-list-item-label" for="task-item-5"> Clear documentation of repository structure.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-6" disabled="disabled"><label class="task-list-item-label" for="task-item-6"> Secrets are not part of the commit history or made public. (see Credential scanning)</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-7" disabled="disabled"><label class="task-list-item-label" for="task-item-7"> Public repositories follow the OSS guidelines, see Required files in default branch for public repositories.</label></li></ul><h2 id="work-item-tracking" tabindex="-1"><a class="header-anchor" href="#work-item-tracking" aria-hidden="true">#</a> Work Item Tracking</h2><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-8" disabled="disabled"><label class="task-list-item-label" for="task-item-8"> All items are tracked in AzDevOps (or similar).</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-9" disabled="disabled"><label class="task-list-item-label" for="task-item-9"> The board is organized (swim lanes, feature tags, technology tags).</label></li></ul><h2 id="testing" tabindex="-1"><a class="header-anchor" href="#testing" aria-hidden="true">#</a> Testing</h2><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-10" disabled="disabled"><label class="task-list-item-label" for="task-item-10"> Unit tests cover the majority of all components (&gt;90% if possible).</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-11" disabled="disabled"><label class="task-list-item-label" for="task-item-11"> Integration tests run to test the solution e2e.</label></li></ul><h2 id="ci-cd" tabindex="-1"><a class="header-anchor" href="#ci-cd" aria-hidden="true">#</a> CI/CD</h2><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-12" disabled="disabled"><label class="task-list-item-label" for="task-item-12"> Project runs CI with automated build and test on each PR.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-13" disabled="disabled"><label class="task-list-item-label" for="task-item-13"> Project uses CD to manage deployments to a replica environment before PRs are merged.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-14" disabled="disabled"><label class="task-list-item-label" for="task-item-14"> Main branch is always shippable.</label></li></ul><h2 id="security" tabindex="-1"><a class="header-anchor" href="#security" aria-hidden="true">#</a> Security</h2><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-15" disabled="disabled"><label class="task-list-item-label" for="task-item-15"> Access is only granted on an as-needed basis</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-16" disabled="disabled"><label class="task-list-item-label" for="task-item-16"> Secrets are stored in secured locations and not checked in to code</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-17" disabled="disabled"><label class="task-list-item-label" for="task-item-17"> Data is encrypted in transit (and if necessary at rest) and passwords are hashed</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-18" disabled="disabled"><label class="task-list-item-label" for="task-item-18"> Is the system split into logical segments with separation of concerns? This helps limiting security vulnerabilities.</label></li></ul><h2 id="observability" tabindex="-1"><a class="header-anchor" href="#observability" aria-hidden="true">#</a> Observability</h2><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-19" disabled="disabled"><label class="task-list-item-label" for="task-item-19"> Significant business and functional events are tracked and related metrics collected.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-20" disabled="disabled"><label class="task-list-item-label" for="task-item-20"> Application faults and errors are logged.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-21" disabled="disabled"><label class="task-list-item-label" for="task-item-21"> Health of the system is monitored.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-22" disabled="disabled"><label class="task-list-item-label" for="task-item-22"> The client and server side observability data can be differentiated.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-23" disabled="disabled"><label class="task-list-item-label" for="task-item-23"> Logging configuration can be modified without code changes (eg: verbose mode).</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-24" disabled="disabled"><label class="task-list-item-label" for="task-item-24"> Incoming tracing context is propagated to allow for production issue debugging purposes.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-25" disabled="disabled"><label class="task-list-item-label" for="task-item-25"> GDPR compliance is ensured regarding PII (Personally Identifiable Information).</label></li></ul><h2 id="agile-scrum" tabindex="-1"><a class="header-anchor" href="#agile-scrum" aria-hidden="true">#</a> Agile/Scrum</h2><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-26" disabled="disabled"><label class="task-list-item-label" for="task-item-26"> Process Lead (fixed/rotating) runs the daily standup</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-27" disabled="disabled"><label class="task-list-item-label" for="task-item-27"> The agile process is clearly defined within team.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-28" disabled="disabled"><label class="task-list-item-label" for="task-item-28"> The Dev Lead (+ PO/Others) are responsible for backlog management and refinement.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-29" disabled="disabled"><label class="task-list-item-label" for="task-item-29"> A working agreement is established between team members and customer.</label></li></ul><h2 id="design-reviews" tabindex="-1"><a class="header-anchor" href="#design-reviews" aria-hidden="true">#</a> Design Reviews</h2><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-30" disabled="disabled"><label class="task-list-item-label" for="task-item-30"> Process for conducting design reviews is included in the Working Agreement.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-31" disabled="disabled"><label class="task-list-item-label" for="task-item-31"> Design reviews for each major component of the solution are carried out and documented, including alternatives.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-32" disabled="disabled"><label class="task-list-item-label" for="task-item-32"> Stories and/or PRs link to the design document.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-33" disabled="disabled"><label class="task-list-item-label" for="task-item-33"> Each user story includes a task for design review by default, which is assigned or removed during sprint planning.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-34" disabled="disabled"><label class="task-list-item-label" for="task-item-34"> Project advisors are invited to design reviews or asked to give feedback to the design decisions captured in documentation.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-35" disabled="disabled"><label class="task-list-item-label" for="task-item-35"> Discover all the reviews that the customer&#39;s processes require and plan for them.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-36" disabled="disabled"><label class="task-list-item-label" for="task-item-36"> Clear non-functional requirements captured (see Non-Functional Requirements Guidance)</label></li></ul><h2 id="code-reviews" tabindex="-1"><a class="header-anchor" href="#code-reviews" aria-hidden="true">#</a> Code Reviews</h2><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-37" disabled="disabled"><label class="task-list-item-label" for="task-item-37"> There is a clear agreement in the team as to function of code reviews.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-38" disabled="disabled"><label class="task-list-item-label" for="task-item-38"> The team has a code review checklist or established process.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-39" disabled="disabled"><label class="task-list-item-label" for="task-item-39"> A minimum number of reviewers (usually 2) for a PR merge is enforced by policy.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-40" disabled="disabled"><label class="task-list-item-label" for="task-item-40"> Linters/Code Analyzers, unit tests and successful builds for PR merges are set up.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-41" disabled="disabled"><label class="task-list-item-label" for="task-item-41"> There is a process to enforce a quick review turnaround.</label></li></ul><h2 id="retrospectives" tabindex="-1"><a class="header-anchor" href="#retrospectives" aria-hidden="true">#</a> Retrospectives</h2><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-42" disabled="disabled"><label class="task-list-item-label" for="task-item-42"> Retrospectives are conducted each week/at the end of each sprint.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-43" disabled="disabled"><label class="task-list-item-label" for="task-item-43"> The team identifies 1-3 proposed experiments to try each week/sprint to improve the process.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-44" disabled="disabled"><label class="task-list-item-label" for="task-item-44"> Experiments have owners and are added to project backlog.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-45" disabled="disabled"><label class="task-list-item-label" for="task-item-45"> The team conducts longer retrospective for Milestones and project completion.</label></li></ul><h2 id="engineering-feedback" tabindex="-1"><a class="header-anchor" href="#engineering-feedback" aria-hidden="true">#</a> Engineering Feedback</h2><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-46" disabled="disabled"><label class="task-list-item-label" for="task-item-46"> The team submits feedback on business and technical blockers that prevent project success</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-47" disabled="disabled"><label class="task-list-item-label" for="task-item-47"> Suggestions for improvements are incorporated in the solution</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-48" disabled="disabled"><label class="task-list-item-label" for="task-item-48"> Feedback is detailed and repeatable</label></li></ul><h2 id="developer-experience-devex" tabindex="-1"><a class="header-anchor" href="#developer-experience-devex" aria-hidden="true">#</a> Developer Experience (DevEx)</h2><p>Developers on the team can:</p><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-49" disabled="disabled"><label class="task-list-item-label" for="task-item-49"> Build/Compile source to verify it is free of syntax errors and compiles.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-50" disabled="disabled"><label class="task-list-item-label" for="task-item-50"> Execute all automated tests (unit, e2e, etc).</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-51" disabled="disabled"><label class="task-list-item-label" for="task-item-51"> Start/Launch end-to-end to simulate execution in a deployed environment.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-52" disabled="disabled"><label class="task-list-item-label" for="task-item-52"> Attach a debugger to started solution or running automated tests, set breakpoints, step through code, and inspect variables.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-53" disabled="disabled"><label class="task-list-item-label" for="task-item-53"> Automatically install dependencies by pressing F5 (or equivalent) in their IDE.</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-54" disabled="disabled"><label class="task-list-item-label" for="task-item-54"> Use local dev configuration values (i.e. .env, appsettings.development.json).</label></li></ul>',27),c=[l];function d(o,b){return s(),t("div",null,c)}const r=e(a,[["render",d],["__file","EngineeringFundamentalsChecklist.html.vue"]]);export{r as default};
